import React from 'react';
import './NewsPanel.css';
import NewsCard from '../NewsCard/NewsCard';

class NewsPanel extends React.Component{
    constructor(){
        super();
        this.state = {news:null};
    }

    componentDidMount(){
        this.loadMoreNews();
    }

    loadMoreNews(){
        this.setState({
            news:[
                {'url':'http://us.cnn.com',
                 'title':"test",
                 'description':"in the end,",
                 'source':'cnn',
                 'urlToImage':'https://www.google.com/imgres?img-0url=http%3A%2F%2Fwww.ttadm.com%2Fimages%2Fanimebaike%2Fyuxingyiye.jpg&imgrefurl=http%3A%2F%2Fwww.ttadm.com%2Fanimebaike%2Fyuxingyiye.html&docid=FY_pvZpup3PALM&tbnid=ZAMOdtRjr5EQhM%3A&vet=10ahUKEwjEv9e3-6fhAhURmuAKHZWpCUMQMwgzKAAwAA..i&w=210&h=250&bih=514&biw=1037&q=%E5%BE%A1%E5%B9%B8%E4%B8%80%E4%B9%9F&ved=0ahUKEwjEv9e3-6fhAhURmuAKHZWpCUMQMwgzKAAwAA&iact=mrc&uact=8',
                 'digest':"3RjuEomJo2601syZbU70Ha==\n",
                 'reason':"Remommend"
                }]
        });
    }
    renderNews(){
        const news_list = this.state.news.map(news =>{
            return(
                <a className= 'list-group-item' key = {news.digest} href='#'>
                    <NewsCard news = {news} />
                </a>
            );
        });

        return(
            <div className = 'container-fluid'>
                <div className = 'list-group'>
                    {news_list}
                </div>
            </div>
        );
    }
    render(){
        if(this.state.news){
            return(
                <div>
                    {this.renderNews()}
                </div>
            );
        }else{
            return(
                <div>
                    Loadinng...
                </div>
            );
        }
    }
}
export default NewsPanel;
